const genCode = () => {
    let code = '';
    while (code.length < 1000) {
        code = code.concat(Math.floor(Math.random() * 2));
    }
    return code;
}


function hack() {
    let side = Array.from(document.getElementsByClassName("div-side"));
    let clear1 = setInterval(() => {
        side[0].innerHTML = genCode();
        side[1].innerHTML = genCode();
        side[2].innerHTML = genCode();
        side[3].innerHTML = genCode();
        side[4].innerHTML = genCode();
        side[5].innerHTML = genCode();
    }, 300)

}
window.document.addEventListener('onload', hack())





const textContainer = document.getElementById('text-container');
const textDiv = document.getElementById('text-div');



function typeEffect(text, speed, index) {
    return new Promise(resolve => {
        function typeChar() {
            if (index < text.length) {
                textDiv.textContent += text.charAt(index);
                index++;

                // Scroll to the bottom of the container
                textContainer.scrollTop = textContainer.scrollHeight;

                setTimeout(typeChar, speed);
            } else {
                resolve();
            }
        }

        typeChar();
    });
}


async function executeHackingCommands() {
    textDiv.textContent = ''; // Clear previous text

    async function printCommands(commands) {
        for (const command of commands) {

            await typeEffect(command + '\n', 1, 0);
            await new Promise(resolve => setTimeout(resolve, command.length * 1 + 250));

        }
    }


    // let startTime = performance.now()
    // setTimeout(function () {
    //     let endTime = performance.now()
    //     let ET = endTime - startTime;
    //     console.log(ET)
    // }, 10000)

    await printCommands(hackingCommands);
}






const letters = "ABCDEFGHIJKLMNOPQRSTUVWXYZ"

let interval = null

async function show() {
    return await new Promise(function (resolve, reject) {
        document.querySelector("h1").onmousemove = event => {
            let iteration = 0

            clearInterval(interval);

            interval = setInterval(() => {

                event.target.innerText = event.target.innerText
                    .split("")
                    .map((letter, index) => {
                        if (index < iteration) {
                            return event.target.dataset.value[index];

                        }
                        return letters[Math.floor(Math.random() * 26)]
                    })
                    .join("");

                if (iteration >= event.target.dataset.value.length) {

                    clearInterval(interval)
                }
                iteration += 1 / 3;
            }, 50);
        }


        resolve()
    })

}
// show()




executeHackingCommands().then(show).then(function visib(){
    const v=document.querySelector("#access")
    const v1=document.querySelector("#text-div")
    v.style.visibility="visible"
    v.animate([{opacity:1},{opacity:0},{opacity:1}],700)

    v1.animate({opacity:[1,0]},{duration:1000,fill:"forwards"}).addEventListener("finish",function(){
        v1.style.display="none" 
    })
})





function loadingfade() {
    const loadingBG = document.getElementById("loading-container")
    const loadingText = document.getElementById("span")

    loadingBG.style.opacity = "0"
    loadingText.style.opacity = "0"

}

function loadingremove() {
    const loading = document.querySelector("#loading-container")
    loading.style.display = "none"
}


const fistvist = localStorage.getItem("visted")



if (fistvist == null) {

    window.setInterval(loadingfade, 9000)
    window.setInterval(loadingremove, 11000)
    localStorage.setItem("visted", 1)
}
else {
    loadingremove();
}